import * as React from "react"
import * as ToastPrimitives from "@radix-ui/react-toast"
import { cva, type VariantProps } from "class-variance-authority"
import { X } from "lucide-react"

import { cn } from "@/lib/utils"

const ToastProvider = ToastPrimitives.Provider

const ToastViewport = React.forwardRef<
  React.ElementRef<typeof ToastPrimitives.Viewport>,
  React.ComponentPropsWithoutRef<typeof ToastPrimitives.Viewport>
>(({ className, ...props }, ref) => (
  <ToastPrimitives.Viewport
    ref={ref}
    className={cn(
      "fixed top-0 z-[100] flex max-h-screen w-full flex-col-reverse p-4 sm:bottom-0 sm:right-0 sm:top-auto sm:flex-col md:max-w-[420px]",
      className
    )}
    {...props}
  />
))
ToastViewport.displayName = ToastPrimitives.Viewport.displayName

const toastVariants = cva(
  "group pointer-events-auto relative flex w-full items-center justify-between space-x-4 overflow-hidden rounded-md border p-6 pr-8 shadow-lg transition-all data-[swipe=cancel]:translate-x-0 data-[swipe=end]:translate-x-[var(--radix-toast-swipe-end-x)] data-[swipe=move]:translate-x-[var(--radix-toast-swipe-move-x)] data-[swipe=move]:transition-none data-[state=open]:animate-in data-[state=closed]:animate-out data-[swipe=end]:animate-out data-[state=closed]:fade-out-80 data-[state=closed]:slide-out-to-right-full data-[state=open]:slide-in-from-top-full data-[state=open]:sm:slide-in-from-bottom-full",
  {
    variants: {
      variant: {
        default: "border bg-background text-foreground",
        destructive:
          "destructive group border-destructive bg-destructive text-destructive-foreground",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  }
)

const Toast = React.forwardRef<
  React.ElementRef<typeof ToastPrimitives.Root>,
  React.ComponentPropsWithoutRef<typeof ToastPrimitives.Root> &
    VariantProps<typeof toastVariants>
>(({ className, variant, ...props }, ref) => {
  return (
    <ToastPrimitives.Root
      ref={ref}
      className={cn(toastVariants({ variant }), className)}
      {...props}
    />
  )
})
Toast.displayName = ToastPrimitives.Root.displayName

const ToastAction = React.forwardRef<
  React.ElementRef<typeof ToastPrimitives.Action>,
  React.ComponentPropsWithoutRef<typeof ToastPrimitives.Action>
>(({ className, ...props }, ref) => (
  <ToastPrimitives.Action
    ref={ref}
    className={cn(
      "inline-flex h-8 shrink-0 items-center justify-center rounded-md border bg-transparent px-3 text-sm font-medium ring-offset-background transition-colors hover:bg-secondary focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 group-[.destructive]:border-muted/40 group-[.destructive]:hover:border-destructive/30 group-[.destructive]:hover:bg-destructive group-[.destructive]:hover:text-destructive-foreground group-[.destructive]:focus:ring-destructive",
      className
    )}
    {...props}
  />
))
ToastAction.displayName = ToastPrimitives.Action.displayName

const ToastClose = React.forwardRef<
  React.ElementRef<typeof ToastPrimitives.Close>,
  React.ComponentPropsWithoutRef<typeof ToastPrimitives.Close>
>(({ className, ...props }, ref) => (
  <ToastPrimitives.Close
    ref={ref}
    className={cn(
      "absolute right-2 top-2 rounded-md p-1 text-foreground/50 opacity-0 transition-opacity hover:text-foreground focus:opacity-100 focus:outline-none focus:ring-2 group-hover:opacity-100 group-[.destructive]:text-red-300 group-[.destructive]:hover:text-red-50 group-[.destructive]:focus:ring-red-400 group-[.destructive]:focus:ring-offset-red-600",
      className
    )}
    toast-close=""
    {...props}
  >
    <X className="h-4 w-4" />
  </ToastPrimitives.Close>
))
ToastClose.displayName = ToastPrimitives.Close.displayName

const ToastTitle = React.forwardRef<
  React.ElementRef<typeof ToastPrimitives.Title>,
  React.ComponentPropsWithoutRef<typeof ToastPrimitives.Title>
>(({ className, ...props }, ref) => (
  <ToastPrimitives.Title
    ref={ref}
    className={cn("text-sm font-semibold", className)}
    {...props}
  />
))
ToastTitle.displayName = ToastPrimitives.Title.displayName

const ToastDescription = React.forwardRef<
  React.ElementRef<typeof ToastPrimitives.Description>,
  React.ComponentPropsWithoutRef<typeof ToastPrimitives.Description>
>(({ className, ...props }, ref) => (
  <ToastPrimitives.Description
    ref={ref}
    className={cn("text-sm opacity-90", className)}
    {...props}
  />
))
ToastDescription.displayName = ToastPrimitives.Description.displayName

type ToastProps = React.ComponentPropsWithoutRef<typeof Toast>

type ToastActionElement = React.ReactElement<typeof ToastAction>

export {
  type ToastProps,
  type ToastActionElement,
  ToastProvider,
  ToastViewport,
  Toast,
  ToastTitle,
  ToastDescription,
  ToastClose,
  ToastAction,
}

client/src/utils/roleAccess.ts.content:
// Role-based access control configuration
export type UserRole = 'admin' | 'user' | 'client';

export interface MenuItem {
  title: string;
  path: string;
  icon: string;
  section: string;
  roles: UserRole[];
  description?: string;
  priority?: number;
  roleSpecificTitle?: Partial<Record<UserRole, string>>;
  roleSpecificIcon?: Partial<Record<UserRole, string>>;
}

// Define which roles can access which features with role-specific customization
export const menuItems: MenuItem[] = [
  {
    title: "Dashboard",
    roleSpecificTitle: {
      admin: "Management Dashboard",
      user: "Technician Dashboard", 
      client: "My Dashboard"
    },
    path: "/",
    icon: "fas fa-tachometer-alt",
    section: "main",
    roles: ["admin", "user", "client"],
    description: "Overview and key metrics",
    priority: 1
  },
  {
    title: "Digital Inspections", 
    roleSpecificTitle: {
      admin: "All Inspections",
      user: "My Inspections"
    },
    path: "/inspections",
    icon: "fas fa-clipboard-check",
    section: "operations",
    roles: ["admin", "user"], // Shop Manager and Technician only
    description: "Vehicle inspection reports",
    priority: 2
  },
  {
    title: "Appointments",
    roleSpecificTitle: {
      admin: "All Appointments",
      user: "My Assignments", 
      client: "My Appointments"
    },
    path: "/appointments", 
    icon: "fas fa-calendar-alt",
    section: "operations",
    roles: ["admin", "user", "client"], // All roles - clients see their own
    description: "Schedule and manage appointments",
    priority: 3
  },
  {
    title: "Repair Orders",
    roleSpecificTitle: {
      admin: "All Work Orders",
      user: "My Work Orders"
    },
    path: "/repair-orders",
    icon: "fas fa-wrench", 
    section: "operations",
    roles: ["admin", "user"], // Shop Manager and Technician (assigned orders only)
    description: "Track repair progress",
    priority: 4
  },
  {
    title: "Job Board",
    roleSpecificTitle: {
      admin: "Work Assignment Board",
      user: "Available Jobs"
    },
    path: "/job-board",
    icon: "fas fa-tasks",
    section: "operations",
    roles: ["admin", "user"], // Shop Manager and Technician only
    description: "View and assign work",
    priority: 5
  },
  {
    title: "Customer Management",
    path: "/customers",
    icon: "fas fa-users",
    section: "customer",
    roles: ["admin"], // Shop Manager only
    description: "Manage customer database",
    priority: 6
  },
  {
    title: "Two-Way Texting",
    path: "/messaging",
    icon: "fas fa-sms",
    section: "customer",
    roles: ["admin"], // Shop Manager only
    description: "Communicate with customers",
    priority: 7
  },
  {
    title: "Reviews Campaign",
    path: "/reviews",
    icon: "fas fa-star",
    section: "customer",
    roles: ["admin"], // Shop Manager only
    description: "Manage customer reviews",
    priority: 8
  },
  {
    title: "My Account",
    path: "/customer-portal",
    icon: "fas fa-user-circle",
    section: "personal",
    roles: ["client"], // Client only - their personal portal
    description: "View your vehicles and services",
    priority: 1
  },
  {
    title: "Invoices & Payments",
    path: "/invoices",
    icon: "fas fa-file-invoice-dollar",
    section: "business",
    roles: ["admin"] // Shop Manager only
  },
  {
    title: "Inventory",
    path: "/inventory",
    icon: "fas fa-boxes",
    section: "business",
    roles: ["admin"] // Shop Manager only
  },
  {
    title: "Reporting",
    path: "/reporting",
    icon: "fas fa-chart-line",
    section: "business",
    roles: ["admin"] // Shop Manager only
  },
  {
    title: "User Management",
    path: "/users",
    icon: "fas fa-users-cog",
    section: "business",
    roles: ["admin"] // Shop Manager only
  },
  {
    title: "Settings",
    path: "/settings", 
    icon: "fas fa-cog",
    section: "business",
    roles: ["admin"] // Shop Manager only
  }
];

export const sections = {
  main: "Main",
  operations: "Operations",
  customer: "Customer Management", 
  business: "Business Management",
  personal: "My Account"
};

// Role display names
export const roleDisplayNames: Record<UserRole, string> = {
  admin: "Shop Manager",
  user: "Technician",
  client: "Client"
};

// Helper function to filter menu items by user role with customization
export function getMenuItemsForRole(userRole: UserRole): MenuItem[] {
  return menuItems
    .filter(item => item.roles.includes(userRole))
    .map(item => ({
      ...item,
      title: item.roleSpecificTitle?.[userRole] || item.title,
      icon: item.roleSpecificIcon?.[userRole] || item.icon
    }))
    .sort((a, b) => (a.priority || 999) - (b.priority || 999));
}

// Helper function to check if user has access to a specific path
export function hasAccess(userRole: UserRole, path: string): boolean {
  // Dashboard is always accessible to authenticated users
  if (path === "/") return true;

  // Check for exact path match or path prefix for nested routes
  const item = menuItems.find(item => 
    item.path === path || path.startsWith(item.path + "/")
  );
  return item ? item.roles.includes(userRole) : false;
}

// Helper function to get role display name
export function getRoleDisplayName(role?: string): string {
  if (!role || !(role in roleDisplayNames)) {
    return "User";
  }
  return roleDisplayNames[role as UserRole];
}

