/* Status color utilities */
.status-red { 
  @apply bg-red-100 text-red-800 border-red-200; 
}

.status-orange { 
  @apply bg-orange-100 text-orange-800 border-orange-200; 
}

.status-green { 
  @apply bg-green-100 text-green-800 border-green-200; 
}

.status-blue { 
  @apply bg-blue-100 text-blue-800 border-blue-200; 
}

/* Sidebar item interactions */
.sidebar-item:hover { 
  @apply bg-accent text-accent-foreground; 
}

.sidebar-item.active { 
  @apply bg-primary text-primary-foreground; 
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: var(--font-sans);
  }

  h1, h2, h3, h4, h5, h6 {
    font-family: var(--font-sans);
  }
}

/* Custom scrollbar styles */
::-webkit-scrollbar {
  width: 6px;
  height: 6px;
}

::-webkit-scrollbar-track {
  @apply bg-muted bg-opacity-30;
}

::-webkit-scrollbar-thumb {
  @apply bg-muted-foreground bg-opacity-30 rounded-full;
}

::-webkit-scrollbar-thumb:hover {
  @apply bg-muted-foreground bg-opacity-50;
}

/* Focus styles for accessibility */
:focus-visible {
  @apply outline-2 outline-offset-2 outline-ring;
}

/* Animation utilities */
@keyframes slideInRight {
  from {
    transform: translateX(100%);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
}

.animate-slide-in-right {
  animation: slideInRight 0.3s ease-out;
}

/* Card hover effects */
.card-hover {
  @apply transition-all duration-200 hover:shadow-md hover:scale-[1.02];
}

/* Pulse animation for loading states */
@keyframes pulse-subtle {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.7;
  }
}

.animate-pulse-subtle {
  animation: pulse-subtle 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

/* Status indicator styles */
.status-indicator {
  @apply inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium border;
}

.status-indicator.healthy {
  @apply bg-green-100 text-green-800 border-green-200;
}

.status-indicator.warning {
  @apply bg-orange-100 text-orange-800 border-orange-200;
}

.status-indicator.error {
  @apply bg-red-100 text-red-800 border-red-200;
}

.status-indicator.info {
  @apply bg-blue-100 text-blue-800 border-blue-200;
}

/* Utility classes for consistent spacing */
.section-spacing {
  @apply space-y-6;
}

.card-spacing {
  @apply space-y-4;
}

/* Print styles */
@media print {
  .no-print {
    display: none !important;
  }

  .print-break {
    page-break-before: always;
  }
}

