                    )}
                  />

                  <FormField
                    control={addForm.control}
                    name="supplierPartNumber"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Supplier Part Number</FormLabel>
                        <FormControl>
                          <Input {...field} data-testid="input-supplier-part-number" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <FormField
                  control={addForm.control}
                  name="notes"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Notes</FormLabel>
                      <FormControl>
                        <Textarea {...field} data-testid="input-notes" />
